mapper.select:
SELECT num, name, title, content, read_count as readCount, write_date as writeDate  
FROM TB_BOARD
WHERE num = #{num}

mapper.update:
    	UPDATE TB_BOARD

    		title = #{title},

	    		content = #{content},

	    	read_count = read_count +1 ,
	    	write_date = #{writeDate}

    	WHERE num = #{num}
